%{RAFEEF GARBI - ELEC 421 - DIGITAL SIGNAL PROCESSING - 2018HW 5 - PROBLEM 1 - student fileDemonstrating the use of the radon transformfor reconstructing an image from projectionor attentuation profile%}clear all; close all; clc;%% MRI IMAGEload mriD=squeeze(D);obj=im2double(D(:,:,13));subplot(221);imshow(obj,[]); title('Original Image');%% COLLECT PROJECTIONSnum_angles=20;angles = linspace(0,180,num_angles);R = radon(obj,angles);% Plot the radon transform%subplot(222);imshow(R,[]); title('Linear Projections');% RECONSTRUCT FROM PROJECTIONSoptions=["nearest","linear","spline","pchip","cubic","v5cubic"];filters=["none","Ram-Lak","Shepp-Logan","Cosine","Hamming","Hann"];outer=1;MSE=[];SNR=[];for i=options    figure(outer);    index=1;    for filter=filters    I = iradon(R,angles,i,filter,128); %back projection reconstruction    subplot(4,3,index);imshow(I,[]); title(sprintf('Reconstruction with Intr %s and filt %s',i,filter));    SE = (I-obj).^2;    MSE = [MSE,mean(SE(:))]; %Mean Squared Error    SNR = [SNR,20*log(norm(obj,'fro')/norm(obj-I,'fro'))]; %Signal-to-Noise Ratio    subplot(4,3,index+6);imshow(abs(I-obj),[]); title(sprintf('Absolute Difference with Int %s and filt %s',i,filter));    index=index+1;    end    outer=outer+1;end